import React from 'react';
import Flippy, { FrontSide, BackSide } from 'react-flippy';

const Card = ({ name, email, phone, website, company, id }) => {
  return (
    <Flippy
      flipOnHover={true}
      flipOnClick={false}
      flipDirection="verizontal"
      className = 'tc dib pa3'
    >
    <FrontSide
      style={{backgroundColor: "lightblue"}}
      className = 'tc dib br3 pa3 ma2 grow bw2'>
      <div>
        <img alt='robots' src={`https://robohash.org/${id}?size=200x200`} />
        <div>
          <h3 style={{fontSize: "16px"}}>{name}</h3>
          <p>{email}</p>
        </div>
      </div>
    </FrontSide>

    <BackSide style={{backgroundColor: "lightblue"}}>
      <div className="lh-copy measure center f6 black-70">
        <h2>Contact Information</h2>
        <h3>Phone: {phone}</h3>
        <h3>Company: {company}</h3>
      </div>
    </BackSide>

    </Flippy>

  );
}

export default Card;

